package clase;

public class EjDouble {
	
	/*
	 * a) x + y + z b) 2 * y + 3 * (x – z) c) x / y d) x % y e) x / (y + z) f) (x /
	 * y) + z g) 2 * x / 3 * y h) 2 * x / (3 * y) i) x * y % z j) x * (y % z) k) 3 *
	 * x – z – 2 * x l) 2 * x / 5 % y m) x - 100 % y % z n) x - y - z * 2
	 */

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double x = 88;
		double y = 3.5;
		double z = -5.2;
		
		// a
		System.out.println(x + y + z);
		
		// b
		System.out.println(2 * y + 3 * (x-z));
		
		// c
		System.out.println(x / y );
		
		// d 
		System.out.println(x % y);
		
		// e
		System.out.println(x / (y + z));
		
		// f
		System.out.println((x / y) + z);
		
		// g
		System.out.println(2 * x / 3 * y);
		
		// h
		System.out.println(2 * x / (3 * y));
		
		// i
		System.out.println( x * y % z );
		
		// j
		System.out.println(x * (y % z));
		
		// k
		System.out.println(3 * x - z - 2 * x);
		
		// l
		System.out.println(2 * x / 5 % y);
		
		// m
		System.out.println(x - 100 % y % z);
		
		// n
		System.out.println(x - y - z * 2);
		
		
		
	}

}






